import React from 'react';
import logo from './logo.svg';
import './App.css';
import Navbar from './React-Components/Navbar';
import AppContainer from './React-Components/AppContainer';

// const onInputChange = (event) => {
// 	console.log(this)
//     this.setState({
//         [event.target.name]: event.target.value
//     })
//     console.log(event.target.name);
// }

// const handleSubmit = (event) => {
// 	console.log("submitted");
// 	alert('An item was submitted: ' + this.state.value);
//     event.preventDefault();
// }
const addRandom = (event) =>{
	const liquorType = ["Whiskey", "Brandy", "Beer", "Gin", "Rum", 
						"Vodka", "Tequila"]

	const liquor = Math.floor(Math.random()*liquorType.length);
	const price = Math.ceil(Math.random()*(liquor+1)*10);
	const quantity = Math.ceil(Math.random()*10);
}

class Item{
	Constructor(name, quantity, price){
		this.name = name;
		this.quantity = quantity;
		this.price = price;
	}
}

class App extends React.Component{

	Constructor(prop){
		this.state = {
			regionCode: 0,
			itemInputs: {
				name: "",
				quantity: "",
				price: ""
			},
			itemLists: []
		}
		this.onInputChange.bind(this);
		this.addItem.bind(this);
	}


	onInputChange(key, value){
	    this.setState({[key]: value})
	}

	addItem(name, quantity, price){
	 	// add to the array
	 	const newItem = new Item(name, quantity, price);
	 	const items = this.state.itemLists;
	 	this.setState({itemLists:items+[newItem]})
	 	// add to the list
	}

	render(){
		return (
		    <div className="App">
		      	<Navbar/>
		      	<AppContainer infos={this.state}
		      				  onInputChange={this.onInputChange}
		      				  addItem={this.addItem}
		      				  />
		    </div>
		);
	}

}

export default App;
