//     this.setState({
//         [event.target.name]: event.target.value
//     })
//     console.log(event.target.name);
// }

// const handleSubmit = (event) => {
// 	console.log("submitted");
// 	alert('An item was submitted: ' + this.state.value);
//     event.preventDefault();
// }
const addRandom = (event) =>{
	const liquorType = ["Whiskey", "Brandy", "Beer", "Gin", "Rum", 
						"Vodka", "Tequila"]

	const liquor = Math.floor(Math.random()*liquorType.length);
	const price = Math.ceil(Math.random()*(liquor+1)*10);
	const quantity = Math.ceil(Math.random()*10);
}

class Item{
	Constructor(name, quantity, price){
		this.state = {
			name: name,
			quantity: quantity,
			price: price
		}
	}
}

class App extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			regionCode: 0,
			itemInputs: {
				name: "",
				quantity: 1,
				price: 9.99
			},
			itemList: []
		}
		this.onInputChange = this.onInputChange.bind(this);
		this.addItem = this.addItem.bind(this);
	}



	onInputChange(name, value){
		console.log("onInputChange");

	    const { itemInputs } = { ...this.state };
	    const currentState = itemInputs;
	    currentState[name] = value;
	    this.setState({ itemInputs: currentState });
	}

	addItem(name, quantity, price){
	 	// add to the array
	 	const newItem = new Item(name, quantity, price);
	 	alert(name+quantity+price);
	 	alert(newItem.state.name)
	 	let items = this.state.itemList.push(newItem);
	 	this.setState({itemLists:items})
	 	console.log(this.state.itemList)
	 	// add to the list
	}

	render(){
		return (
		    <div className="App">
		      	<Navbar/>
		      	<AppContainer itemInputs={this.state.itemInputs}
		      				  itemList={this.state.itemList}
		      				  onInputChange={this.onInputChange}
		      				  addItem={this.addItem}
		      				  />
		    </div>
		);
	}

}

export default App;
