import React from 'react';
import './style.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';

class NewItemForm extends React.Component{

	onInputChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

	render(){

		return(
			<Form className="newItemForm">
			  	<Form.Group>
				    <Form.Label>Item Name</Form.Label>
				    <Form.Control type="text" onChange={onInputChange} placeholder="Enter Item Name" />
			  	</Form.Group>

			  	<Form.Row>
				    <Col>
				      	<Form.Group>
							<Form.Label>Item Quantity</Form.Label>
						    <Form.Control type="number" onChange={onInputChange} placeholder="1" />
						</Form.Group>
				    </Col>
				    <Col>
				     	<Form.Group>
						    <Form.Label>Item Price</Form.Label>
						    <Form.Control type="number" onChange={onInputChange} step={0.05} placeholder="9.99" />
						</Form.Group>

				    </Col>
				  </Form.Row>

			  <Button variant="primary" type="submit">
			    Add to your chart
			  </Button>
			  <Form.Text>
			  	Or
			  </Form.Text>
			  <Button variant="primary" type="submit">
			    Add Random
			  </Button>
			</Form>
		);
	}
}

export default NewItemForm;
