import React from 'react';
import './style.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';

class NewItemForm extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			name:"",
			quantity:"",
			price:""
		}
	}


	render(){
		const { itemName, itemQuantity, itemPrice, inputChange, itemSubmit } = this.props;
		return(
			<Form className="newItemForm">
			  	<Form.Group>
				    <Form.Label>Item Name</Form.Label>
				    <Form.Control type="text" required value={itemName} onChange={inputChange} placeholder="Enter Item Name" />
			  	</Form.Group>

			  	<Form.Row>
				    <Col>
				      	<Form.Group>
							<Form.Label>Item Quantity</Form.Label>
						    <Form.Control type="number" required value={itemQuantity} onChange={inputChange} placeholder="1" />
						</Form.Group>
				    </Col>
				    <Col>
				     	<Form.Group>
						    <Form.Label>Item Price</Form.Label>
						    <Form.Control type="number" required value={itemPrice} onChange={inputChange} step={0.05} placeholder="9.99" />
						</Form.Group>

				    </Col>
				  </Form.Row>

			  <Button variant="primary" type="submit">
			    Add to your chart
			  </Button>
			  <Form.Text>
			  	Or
			  </Form.Text>
			  <Button variant="primary" type="submit">
			    Add Random
			  </Button>
			</Form>
		);
	}
}

export default NewItemForm;
