import './style.css';
import React from 'react';
import NavbarRB from 'react-bootstrap/Navbar';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';
import NavDropdown from 'react-bootstrap/NavDropdown';

class Navbar extends React.Component{
	constructor(props){
		super(props);
		this.addPromo = this.addPromo.bind(this);
		this.onInputChange = this.onInputChange.bind(this);
		this.changeRegion = this.changeRegion.bind(this);
	}

	addPromo(event){
		if (this.props.promoCode!=""){
			event.preventDefault();
			this.props.addPromo(this.props.promoCode);
		}

	}

	onInputChange(event){
		console.log(event.target.id)
		this.props.onInputChange(event.target.id, event.target.value, 2);
	}

	changeRegion(event){
		console.log(event.target.value);
		console.log(event.target.id);
		this.props.changeRegion(event.target.id);
	}

	render(){
		const { regionCode, changeRegion, addPromo, promoCode, onInputChange } = this.props;
		// const regionsDic = {0: "Alberta", 1: "British Columbia", 2:"Manitoba", 3:"New Brunswick", 
		// 4:"Newfoundland", 5:"Northwest Territories", 6:"Nova Scotia", 
		// 7:"Nunavut", 8:"Ontario", 9:"Prince Edward Island", 10:"Quebec", 11:"Saskatchewan",
		// 12:"Yukon"};
		const regionsList = ["Select Region", "Alberta", "British Columbia", "Manitoba", "New Brunswick", 
		"Newfoundland", "Northwest Territories", "Nova Scotia", 
		"Nunavut", "Ontario", "Prince Edward Island", "Quebec", "Saskatchewan",
		"Yukon"];
		const provinceList = ["Alberta", "British Columbia", "Manitoba", "New Brunswick", 
		"Newfoundland", "Nova Scotia", "Ontario", "Prince Edward Island", "Quebec", "Saskatchewan"];
		const terriList = ["Northwest Territories", "Nunavut", "Yukon"];

		const provinceSelect = provinceList.map((value, index)=>
			<NavDropdown.Item id={index} value={index}>{value}</NavDropdown.Item>
		);
		const terriSelect = terriList.map((value, index)=>
			<NavDropdown.Item id={index} value={index}>{value}</NavDropdown.Item>
		);

		const regionSelect = regionsList.map((value, index)=>
			<NavDropdown.Item className={regionCode==index ? "selected":"unselected"}
							id={index} value={index} onClick={this.changeRegion}>
				{value}
			</NavDropdown.Item>
		);
		const regionOption = regionsList.map((value,index)=>
			<option id={index}>{value}</option>
		);
		const curRegion = regionCode ? regionsList[regionCode] :"Select Region";
		return (
			<div>
			<NavbarRB variant="dark" bg="dark" expand="lg">
			  <NavbarRB.Brand className="navbarBrand" href="">
				<img 
			        alt=""
			        src="https://cdn.clipart.email/d8a63873616061abe7766d804101fd26_online-shopping-cart-logo-png-png-image_256-256.png"
			        className="d-inline-block align-top"
			      />{' '}
			      Checkout App
			    </NavbarRB.Brand>
			  <NavbarRB.Toggle aria-controls="basic-navbar-nav" />
			  <NavbarRB.Collapse id="basic-navbar-nav">
			    <Nav className="mr-auto">
			      	<Nav.Link href="#home">Home</Nav.Link>
			      	<NavDropdown title={curRegion} id="basic-nav-dropdown">
				        {regionSelect}
				    </NavDropdown>
			    </Nav>
			    <Form inline>
			      <FormControl type="text" placeholder="promo code" value={promoCode} 
			      				onChange={this.onInputChange} id="promoCode" 
			      				required className="mr-sm-2" />
			      <Button variant="outline-success" type="submit"
			      			onClick={this.addPromo}>Discount</Button>
			    </Form>
			  </NavbarRB.Collapse>
			</NavbarRB>
			</div>

		)
	}
}

export default Navbar;
