import React from 'react';
import './style.css';
import { Container, Row, Col, Button, Card } from 'react-bootstrap';

class Item extends React.Component{

	constructor(props){
		super(props)
		this.state={
			itemId: this.props.itemId
		}
		this.removeItem = this.removeItem.bind(this);
	}

	removeItem(){
		console.log(this.state.itemId);
		console.log(this)
		this.props.removeItem(this.state.itemId)
	}

	render(){
		const { itemName, itemQuantity, itemPrice, itemId, removeItem } = this.props;

		return(
			<Card border="info">
				<Row className="itemRow">
					<Col lg="2">{itemName}
					</Col>
					<Col lg="auto">
						<Container>
							<Row>
								<Col>Quantity: {itemQuantity}</Col>
								<Container>
									<Row>-</Row>
									<Row>+</Row>
								</Container>
							</Row>
						</Container>
					</Col>
					<Col>${itemPrice}
					</Col>
					<Col>
						<Button variant="outline-danger" 
								size="sm"
								onClick={this.removeItem}>
							Delete
						</Button>
					</Col>
				</Row>
			</Card>
		)
	}
}

export default Item;
