import React from 'react';
import './style.css'
import Item from '../Item/index.js';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';

class ItemContainer extends React.Component{



	render(){
		const { itemList, removeItem, total } = this.props;
		const theList = itemList.map((item)=>
			<Item itemName={item.name} itemQuantity={item.quantity} 
				itemPrice={item.price} itemId={item.id} removeItem={removeItem}>
			</Item>
		)

		return(
			<div className="itemContainer">
				<Navbar bg="info" variant="dark" sticky>
				    <Navbar.Brand>Your Cart</Navbar.Brand>
				</Navbar>
				{theList}
				<Navbar className="itemTotal" bg="danger" variant="dark" fixed="buttom">
				    <Navbar.Brand>Your Total: ${total.toFixed(2)}</Navbar.Brand>
				    <Button type="submit">Checkout</Button>
				</Navbar>
			</div>
		)
	}
}

export default ItemContainer;
