import React from 'react';
import logo from './logo.svg';
import './App.css';
import Navbar from './React-Components/Navbar';
import AppContainer from './React-Components/AppContainer';

class App extends React.Components{
	onInputChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handleSubmit = (event) => {
	    alert('An item was submitted: ' + this.state.value);
	    event.preventDefault();
	}

	state = {
		regionCode: 0,
		newItemName: "",
		newItemQuantity: "",
		newItemPrice: "",
		onInputChange: onInputChange.bind(this),
		handleSubmit: handleSubmit.bind(this)
	}



	render(){
		return (
		    <div className="App">
		      	<Navbar/>
		      	<AppContainer infos={this.state.bind(this)}
		      				  />
		    </div>
		);
	}

}

export default App;
