import React from 'react';
import './style.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';

class NewItemForm extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			name:"",
			quantity:"",
			price:""
		}
		this.inputChange = this.inputChange.bind(this);
		this.itemSubmit = this.itemSubmit.bind(this);
	}

	 inputChange(event){
	    this.setState({
	        [event.target.name]: event.target.value
	    })
	    console.log(event.target.name);
	}

	 itemSubmit(event){
		console.log("submitted");
		alert('An item was submitted: ' + this.state.value);
	    event.preventDefault();
	}
	render(){
		// const { itemName, itemQuantity, itemPrice, inputChange, itemSubmit } = this.props;
		return(
			<Form className="newItemForm">
			  	<Form.Group>
				    <Form.Label>Item Name</Form.Label>
				    <Form.Control className="name" type="text" required 
				    value={this.state.name} onChange={this.inputChange} 
				    placeholder="Enter Item Name" />
			  	</Form.Group>

			  	<Form.Row>
				    <Col>
				      	<Form.Group>
							<Form.Label>Item Quantity</Form.Label>
						    <Form.Control className="quantity" type="number" 
						    required value={this.state.quantity} 
						    onChange={this.inputChange} placeholder="1" />
						</Form.Group>
				    </Col>
				    <Col>
				     	<Form.Group>
						    <Form.Label>Item Price</Form.Label>
						    <Form.Control className="price" type="number" 
						    required value={this.state.price} 
						    onChange={this.inputChange} step={0.05} placeholder="9.99" />
						</Form.Group>

				    </Col>
				  </Form.Row>

			  <Button variant="primary" type="submit">
			    Add to your chart
			  </Button>
			  <Form.Text>
			  	Or
			  </Form.Text>
			  <Button variant="primary" type="submit">
			    Add Random
			  </Button>
			</Form>
		);
	}
}

export default NewItemForm;
