import React from 'react';
import './style.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';

class NewItemForm extends React.Component{

	constructor(props){
		super(props);
		this.inputChange = this.inputChange.bind(this);
		this.itemSubmit = this.itemSubmit.bind(this);
	}

	inputChange(event){
		this.props.inputChange(event.target.id, event.target.value);
	    // this.setState({
	    //     [event.target.id]: event.target.value
	    // })
	}

	 itemSubmit(event){
	 	// add to the array
	 	this.props.itemSubmit(this.itemName, this.itemQuantity, this.itemPrice)
    	event.preventDefault();

	 	console.log(event)
	 	// this.props.itemSubmit(event)
	 	// add to the list
	}

	render(){
		const { itemName, itemQuantity, itemPrice, inputChange, itemSubmit } = this.props;
		return(
			<Form className="newItemForm">
			  	<Form.Group>
				    <Form.Label>Item Name</Form.Label>
				    <Form.Control id="name" type="text" required 
				    value={itemName} onChange={this.inputChange} 
				    placeholder="Enter Item Name" />
			  	</Form.Group>

			  	<Form.Row>
				    <Col>
				      	<Form.Group>
							<Form.Label>Item Quantity</Form.Label>
						    <Form.Control id="quantity" type="number" required 
						    value={itemQuantity} 
						    onChange={this.inputChange} placeholder="1" />
						</Form.Group>
				    </Col>
				    <Col>
				     	<Form.Group>
						    <Form.Label>Item Price</Form.Label>
						    <Form.Control id="price" type="number" required 
						    value={itemPrice} onChange={this.inputChange} 
						    step={0.05} placeholder="9.99" />
						</Form.Group>

				    </Col>
				  </Form.Row>

			  <Button variant="primary" type="submit" onClick={this.itemSubmit}>
			    Add to your chart
			  </Button>
			  <Form.Text>
			  	Or
			  </Form.Text>
			  <Button variant="primary" type="submit">
			    Add Random
			  </Button>
			</Form>
		);
	}
}

export default NewItemForm;
