import './style.css';
import React from 'react';
import NavbarRB from 'react-bootstrap/Navbar';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';

class Navbar extends React.Component{
	constructor(){
		this.state={
			promoCode:this.props.promoCode
		}
		this.addPromo = this.props.addPromo.bind();
		this.onInputChange = this.props.onInputChange.bind();
	}

	addPromo(){
		this.props.addPromo(this.state.promoCode);
	}

	onInputChange(event){
		this.props.inputChange(event.target.id, event.target.value);
	}

	render(){
		const { addPromo, onInputChange, promoCode } = this.props;

		return (
			<div>
			<NavbarRB variant="dark" bg="dark" expand="lg">
			  <NavbarRB.Brand className="navbarBrand" href="">
				<img 
			        alt=""
			        src="https://cdn.clipart.email/d8a63873616061abe7766d804101fd26_online-shopping-cart-logo-png-png-image_256-256.png"
			        className="d-inline-block align-top"
			      />{' '}
			      Checkout App
			    </NavbarRB.Brand>
			  <NavbarRB.Toggle aria-controls="basic-navbar-nav" />
			  <NavbarRB.Collapse id="basic-navbar-nav">
			    <Nav className="mr-auto">
			      <Nav.Link href="#home">Home</Nav.Link>
			      <Nav.Link href="#link">Change Region</Nav.Link>
			    </Nav>
			    <Form inline>
			      <FormControl type="text" placeholder="promo code" value={promoCode} 
			      				onChange={onInputChange} id="promoCode" className="mr-sm-2" />
			      <Button variant="outline-success" onClick={addPromo}>Discount</Button>
			    </Form>
			  </NavbarRB.Collapse>
			</NavbarRB>
			</div>

		)
	}
}

export default Navbar;
